mapper.insert:
UPDATE TB_BOARD
SET   BOARD_VIEW_COUNT = (BOARD_VIEW_COUNT + 1)

	WHERE BOARD_NO = #{boardNo}

UPDATE TB_BOARD SET BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent}, STATUS = #{status}, FILE_PATH = #{filePath}
WHERE 1=1
AND BOARD_NO = #{boardNo}

mapper.delete:
DELETE  FROM  TB_BOARD
WHERE   1=1

AND     BOARD_NO = #{boardNo}
